package programmers.kakao.blind2018;

import java.util.Stack;

public class DartGame {
    public int solution(String dartResult) {
        int answer = 0;
        Stack<Integer> answerStk = new Stack<>();
        for (int i = 0; i < dartResult.length(); i++) {
