package programmers.school.day03;

import java.util.LinkedList;
import java.util.Queue;

public class Network {
	// ë¨¼ì € ë°°ì—´ì˜ ê°’ë§Œí¼ boolean ë°°ì—´ì„ ë§Œë“¤ì–´ì£¼ê³  í•˜ë‚˜ì”© ê°’ì„ ë„£ê³ 
	// ê±°ê¸°ì— ì—°ê²°ë˜ì–´ìˆëŠ” ë°°ì—´ë¡œ ë“¤ì–´ê°€ ì°¾ê¸° (BFS)
	public int solution(int n, int[][] computers) {
		boolean[] isVisted = new boolean[n];
		int answer = 0;
		Queue<Integer> que = new LinkedList<Integer>();

		for (int i = 0; i < n; i++) {
			if (isVisted[i])
				continue;
			isVisted[i] = true;
			que.add(i);
			answer++;

			while (!que.isEmpty()) {
				int[] conn = computers[que.poll()];
				for (int j = 0; j < conn.length; j++) {
					if(conn[j] == 1 && !isVisted[j]) {
						isVisted[j] = true;
						que.add(j);
					}
				}
			}
		}
		return answer;
	}
}

