			if (durability[i] <= 0)
				continue;
			isOne = false;
			durability[i] -= weight[index];
			durability[index] -= weight[i];
			go(index + 1);
			durability[i] += weight[index];
			durability[index] += weight[i];
		}
		if(isOne) {
			go(index + 1);
		}
	}

}

