		for (int i = 0; i < scoville.length; i++) {
			list.offer(scoville[i]);
		}
		while (list.peek() < K) {
			if (list.size() >= 2) {
				int min = list.poll();
				int min2 = list.poll();

				int hotter = min + (min2 * 2);
				list.offer(hotter);
				answer++;
			}else {
