	}

	public static boolean check(int x, int y, int dir) {
		boolean answer = true;
		int[] heights = new int[N];
		if (dir == 0) {
			for (int i = 0; i < N; i++) {
				heights[i] = map[y][i];
			}
		} else if (dir == 1) {
			for (int i = 0; i < N; i++) {
				heights[i] = map[i][x];
			}
		}

		int now = heights[0];
		int heights_n = 1;
		int down = L;

