//		} ë§µ ì¶œë ¥

	}

	static class Snake {
		Deque<Point> body = new LinkedList<Point>();
		int dir = 0;

		public Snake() {
			body.addLast(new Point(1, 1));
		}

		public void go() {
			int y = body.peekLast().y + DIR[dir][1];
			int x = body.peekLast().x + DIR[dir][0];


			for (Point pt : body) {
				if (pt.x == x && pt.y == y) {
					isGame = false;
				}
			}
			if (map[y][x] == 1) {
				isGame = false;
				return;
			} else if (map[y][x] == 0) {
				body.pollFirst();
			} else if (map[y][x] == 3) {
				map[y][x] = 0;
			}

			body.addLast(new Point(x, y));
		}

		public void changeDir(char c) {
			if (c == 'D') {
				dir = (dir + 1) % 4;
			} else if (c == 'L') {
				dir = dir - 1 >= 0 ? dir - 1 : 3;
			}
		}

	}
}

