//	 * 3(ë¶) => ë°‘ë©´ 2 { 1, 4, 5, 3, 6, 2} (0 -> 5) (5 -> 4) (4 -> 2) (2 -> 0) 
//	 * 4(ë‚¨) => ë°‘ë©´ 5 { 0, 0, 0, 0, 0, 0} (4 -> 5) (5 -> 0) (0 -> 2) (2 -> 5)

	public static void move(int dir) {

		int[] temp_dice = new int[6];
		switch (dir) {
		case 1:
			temp_dice[0] = dice[0];
			temp_dice[1] = dice[5];
			temp_dice[2] = dice[1];
			temp_dice[3] = dice[2];
			temp_dice[4] = dice[4];
			temp_dice[5] = dice[3];
			break;
		case 2:
			temp_dice[0] = dice[0];
			temp_dice[1] = dice[2];
			temp_dice[2] = dice[3];
			temp_dice[3] = dice[5];
			temp_dice[4] = dice[4];
			temp_dice[5] = dice[1];
			break;
		case 3:
			temp_dice[0] = dice[2];
			temp_dice[1] = dice[1];
			temp_dice[2] = dice[4];
			temp_dice[3] = dice[3];
			temp_dice[4] = dice[5];
			temp_dice[5] = dice[0];
			break;
		case 4:
			temp_dice[0] = dice[5];
			temp_dice[1] = dice[1];
			temp_dice[2] = dice[0];
			temp_dice[3] = dice[3];
			temp_dice[4] = dice[2];
			temp_dice[5] = dice[4];
			break;
		default:
			break;
		}
		dice = temp_dice;
	}

	public static boolean inMap(int dir) {
		boolean isIn = true;
		int x_m = x + DIR[dir][0];
		int y_m = y + DIR[dir][1];
		if (x_m >= 0 && x_m < width) {
			x = x_m;
		} else {
			return false;
		}
		if (y_m >= 0 && y_m < height) {
			y = y_m;
		} else {
			return false;
		}
		return isIn;
	}

	public static void getMap() {
		if (map[y][x] == 0) {
			map[y][x] = dice[5];
		} else {
			dice[5] = map[y][x];
			map[y][x] = 0;
		}
	}
}

